const SORT_TYPE = {
  titleAscend: 'title_ascend',
  titleDecend: 'title_descend',
  releaseDateAscend: 'release_date_ascend',
  releaseDateDescend: 'release_date_descend',
  voteCountAscend: 'vote_count_ascend',
  voteCountDescend: 'vote_count_descend',
  voteAverageAscend: 'vote_average_ascend',
  voteAverageDescend: 'vote_average_descend',
};

